#include<bits/stdc++.h>using namespace std;int tot,i,j,t[2],k,xj,xk;long long a,ans,s;int main(){    scanf("%d",&tot);    while(tot--)    {    	scanf("%lld",&a);    	if(a%3==0)    	{    		printf("1 %lld\n",a);    		continue;    	}    	memset(t,0,sizeof(t));    	for(i=0;i<=62;i++)    		if((a>>i)&1)    			t[i%2]++;    	ans=0;    	xj=xk=0;    	for(j=t[0];j>=(t[0]+1)/2&&xj==0;j--)    		for(k=t[1];k>=(t[1]+1)/2;k--)    			if((j+k*2)%3==0)    			{    				xj=j;    				xk=k;					break;				}		int tj=xj,tk=xk;		if(t[0]==1&&t[1]==3)		{			tj=1;			tk=1;		}		if(t[1]==1&&t[0]==3)		{			tj=3;			tk=0;		}    	for(j=0;j<=62&&tj;j+=2)    		if((a>>j)&1)    		{				tj--;    			ans^=1ll<<j;    		}    	for(j=1;j<=62&&tk;j+=2)    		if((a>>j)&1)    		{				tk--;    			ans^=1ll<<j;    		}    	printf("2 %lld ",ans);    	ans=0;    	tj=xj,tk=xk;    	if(t[0]==1&&t[1]==3)		{			tj=0;			tk=3;		}		if(t[1]==1&&t[0]==3)		{			tj=1;			tk=1;		}    	for(j=62;j>=0&&tj;j-=2)    		if((a>>j)&1)    		{				tj--;    			ans^=1ll<<j;    		}    	for(j=61;j>=0&&tk;j-=2)    		if((a>>j)&1)    		{				tk--;    			ans^=1ll<<j;    		}    	printf("%lld\n",ans);	}}
